rowClick.customFunction:
var $injector = widgetContext.$scope.$injector;

$injector.get('deviceService').getDevice(entityId.id).then(function(device) {
 if(device.name == 'AC1') {
  openDashboardState('acelerometro_1');
 } else if(device.name == 'AC2') {
  openDashboardState('acelerometro_2');
 } else if(device.name == 'AC3') {
  openDashboardState('acelerometro_3');
 } else if(device.name == 'AC4') {
  openDashboardState('acelerometro_4');
 }
});

function openDashboardState(stateId) {
 var params = {
 entityId: entityId,
 entityName: entityName
 };

 widgetContext.stateController.openState(stateId, params, false);
}

